        <span class="token keyword">for</span><span class="token punctuation">(</span><span class="token keyword">int</span> i<span class="token operator">=</span><span class="token number">0</span><span class="token punctuation">,</span>upper<span class="token operator">=</span>isLimit<span class="token operator">?</span>c<span class="token punctuation">[</span>index<span class="token punctuation">]</span><span class="token operator">-</span><span class="token char">'0'</span><span class="token operator">:</span><span class="token number">9</span><span class="token punctuation">;</span>i<span class="token operator">&lt;=</span>upper<span class="token punctuation">;</span>i<span class="token operator">++</span><span class="token punctuation">)</span><span class="token punctuation">{</span>
